package examples;

import com.onrender.restaurantapii70b.QrBasedRestaurantManagementSystemApiClient;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.slf4j.event.Level;

public class App {
    private static final Logger CUSTOM_LOGGER = LoggerFactory.getLogger(App.class);

    public static void main(String[] args) {
        QrBasedRestaurantManagementSystemApiClient client = new QrBasedRestaurantManagementSystemApiClient.Builder()
            .loggingConfig(builder -> builder
                .logger(CUSTOM_LOGGER)
                .level(Level.DEBUG)
                .requestConfig(logConfigBuilder -> logConfigBuilder.body(true))
                .responseConfig(logConfigBuilder -> logConfigBuilder.headers(true)))
            .build();
    }
}

